import React, { useEffect, useState } from "react";
import {
  View,
  Text,
  Picker,
  TextInput,
  Button,
  StyleSheet,
} from "react-native";
import axios from "axios";
import DatePicker from "react-datepicker";

import "react-datepicker/dist/react-datepicker.css";

const RDVScreen = ({ navigation, route }) => {
  const { agency, service, token } = route.params;
  const [selectedOption, setSelectedOption] = useState("1");
  const [selectedDate, setSelectedDate] = useState("");
  const [cars, setCars] = useState([]);

  useEffect(() => {
    getCars();
  }, []);

  async function getCars() {
    await axios
      .get("http://127.0.0.1:8000/api/cars", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((resp) => {
        setCars(resp.data.data);
      });
  }

  const handleSaveRDV = () => {
    // Implement your logic to save the RDV
    // You can use selectedOption and selectedDate
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>
        Rendez-vous chez "{agency.name}" pour "{service.service}"
      </Text>

      <View style={styles.formGroup}>
        <Text style={styles.label}>Selectionner ta voiture:</Text>
        <Picker
          selectedValue={selectedOption}
          onValueChange={(itemValue) => setSelectedOption(itemValue)}
        >
          {cars.map((car, index) => (
            <Picker.Item key={index} label={car.model} value={car.id} />
          ))}
        </Picker>
      </View>

      <View style={styles.formGroup}>
        <Text style={styles.label}>Selectionner a Date:</Text>
        <DatePicker
          selected={selectedDate}
          onChange={(date) => setSelectedDate(date)}
          dateFormat="yyyy-MM-dd"
        />
      </View>

      <Button title="Save RDV" onPress={handleSaveRDV} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: "bold",
    marginBottom: 20,
  },
  formGroup: {
    marginBottom: 20,
  },
  label: {
    fontSize: 16,
    marginBottom: 5,
  },
});

export default RDVScreen;
