import React, { useEffect, useState } from "react";
import {
  View,
  Text,
  Picker,
  TextInput,
  Button,
  StyleSheet,
  Platform,
  DatePickerIOS,
  DatePickerAndroid,
} from "react-native";
import axios from "axios";

const RDVScreen = ({ navigation, route }) => {
  const token = route.params.token;
  const [selectedOption, setSelectedOption] = useState("1");
  const [selectedDate, setSelectedDate] = useState("");
  const [cars, setCars] = useState([]);

  useEffect(() => {
    getCars();
  }, []);

  async function getCars() {
    await axios
      .get("http://127.0.0.1:8000/api/cars", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((resp) => {
        setCars(resp.data.data);
      });
  }

  const handleSaveRDV = () => {
    // Implement your logic to save the RDV
    // You can use selectedOption and selectedDate
  };

  const showDatePicker = async () => {
    if (Platform.OS === "android") {
      try {
        const { action, year, month, day } = await DatePickerAndroid.open({
          date: selectedDate,
        });
        if (action !== DatePickerAndroid.dismissedAction) {
          const newDate = new Date(year, month, day);
          setSelectedDate(newDate);
        }
      } catch ({ code, message }) {
        console.warn("Cannot open date picker", message);
      }
    } else if (Platform.OS === "ios") {
      // You can show the DatePickerIOS directly
      // DatePickerIOS works only on iOS
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Create a New RDV</Text>

      <View style={styles.formGroup}>
        <Text style={styles.label}>Select an Option:</Text>
        <Picker
          selectedValue={selectedOption}
          onValueChange={(itemValue) => setSelectedOption(itemValue)}
        >
          {cars.map((car, index) => (
            <Picker.Item key={index} label={car.model} value={car.id} />
          ))}
        </Picker>
      </View>

      <View style={styles.formGroup}>
        <Text style={styles.label}>Select a Date:</Text>
        {Platform.OS === "ios" ? (
          <DatePickerIOS
            mode="date"
            date={selectedDate}
            onDateChange={(newDate) => setSelectedDate(newDate)}
          />
        ) : (
          <Button title="Pick a Date" onPress={showDatePicker} />
        )}
      </View>

      <Button title="Save RDV" onPress={handleSaveRDV} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: "bold",
    marginBottom: 20,
  },
  formGroup: {
    marginBottom: 20,
  },
  label: {
    fontSize: 16,
    marginBottom: 5,
  },
  dateInput: {
    height: 40,
    borderWidth: 1,
    borderColor: "#ccc",
    borderRadius: 5,
    paddingHorizontal: 10,
  },
});

export default RDVScreen;
