import React, { useState } from "react";
import {
  View,
  Text,
  TouchableOpacity,
  StyleSheet,
  TextInput,
} from "react-native";
import { AntDesign, FontAwesome, MaterialIcons } from "@expo/vector-icons";

const HomeScreen = ({ navigation, route }) => {
  const token = route.params.token;
  const [searchValue, setsearchValue] = useState("");

  const handleLogin = async () => {
    try {
      await axios
        .post("http://127.0.0.1:8000/api/login", {
          email,
          password,
        })
        .then((resp) => {
          const token = resp.data.user.token;
          navigation.navigate("Home", { token });
        });
      // Store the token in AsyncStorage or secure storage
    } catch (error) {
      console.error("Login error:", error);
    }
  };
  return (
    <View style={styles.container}>
      <View style={styles.content}>
        <Text style={styles.title}>vous cherchez une agence ?</Text>
        <TextInput
          placeholder="entrer votre emplacement"
          value={searchValue}
          onChangeText={setsearchValue}
          style={styles.input}
        />
        <View style={styles.loginButton}>
          <Button title="Connexion" onPress={handleLogin} />
        </View>
      </View>
      <View style={styles.buttonContainer}>
        <TouchableOpacity style={styles.button}>
          <AntDesign name="home" size={24} color="black" />
          <Text
            style={styles.buttonLabel}
            onPress={() => navigation.navigate("Home")}
          >
            Accueil
          </Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.button}>
          <AntDesign name="user" size={24} color="black" />
          <Text
            style={styles.buttonLabel}
            onPress={() => navigation.navigate("Users", { token })}
          >
            Utilisateurs
          </Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={styles.button}
          onPress={() => navigation.navigate("Agencies", { token })}
        >
          <FontAwesome name="building" size={24} color="black" />
          <Text style={styles.buttonLabel}>Agences</Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={styles.button}
          onPress={() => navigation.navigate("Cars", { token })}
        >
          <MaterialIcons name="directions-car" size={24} color="black" />
          <Text style={styles.buttonLabel}>Voitures</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
  },
  content: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
  },
  title: {
    fontSize: 20,
    fontWeight: "bold",
    marginBottom: 20,
  },
  buttonContainer: {
    flexDirection: "row",
    justifyContent: "space-around",
    alignItems: "center",
    paddingBottom: 20,
  },
  button: {
    alignItems: "center",
  },
  buttonLabel: {
    marginTop: 5,
    fontSize: 16,
  },
  input: {
    width: 200,
    height: 40,
    borderColor: "gray",
    borderWidth: 1,
    marginVertical: 10,
    paddingHorizontal: 8,
    backgroundColor: "white",
    opacity: 1,
  },
  loginButton: {
    margin: 10,
  },
});

export default HomeScreen;
