import React, { useState, useEffect } from "react";
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  FlatList,
  StyleSheet,
} from "react-native";

const CarsManagementCRUDScreen = () => {
  const [cars, setCars] = useState([]);
  const [marque, setMarque] = useState("");
  const [model, setModel] = useState("");
  const [year, setYear] = useState("");
  const [matricule, setMatricule] = useState("");

  useEffect(() => {
    getCars();
  }, []);

  async function getCars() {
    await axios
      .get("http://127.0.0.1:8000/api/cars", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((resp) => {
        setCars(resp.data.data);
      });
  }

  const handleAddCar = async () => {
    try {
      await axios
        .post(
          "http://127.0.0.1:8000/api/cars",
          {
            marque,
            model,
            year,
            matricule,
          },
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        )
        .then((resp) => {
          getCars();
          setMarque("");
          setModel("");
          setYear("");
          setMatricule("");
        });
    } catch (error) {
      console.error("Create Car error:", error);
    }
  };

  const handleEditCar = (car) => {
    // Handle editing a car logic
    const carToEdit = cars.find((car) => car.id === carId);
    setSelectedCarId(carId);
    setMarque(carToEdit.marque);
    setModel(carToEdit.model);
    setYear(carToEdit.year);
    setMatricule(carToEdit.matricule);
  };

  const handleUpdateCar = () => {
    // Handle updating the car logic
    const updatedCars = cars.map((car) =>
      car.id === selectedCarId
        ? { ...car, marque, model, year, matricule }
        : car
    );

    setCars(updatedCars);

    // Clear input fields and selectedCarId
    setSelectedCarId(null);
    setMarque("");
    setModel("");
    setYear("");
    setMatricule("");
  };

  const handleDeleteCar = (carId) => {
    getCars();
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Cars Management (CRUD)</Text>
      <View style={styles.inputContainer}>
        <TextInput
          style={styles.input}
          placeholder="Marque"
          value={marque}
          onChangeText={setMarque}
        />
        <TextInput
          style={styles.input}
          placeholder="Model"
          value={model}
          onChangeText={setModel}
        />
        <TextInput
          style={styles.input}
          placeholder="Year"
          value={year}
          onChangeText={setYear}
          keyboardType="numeric"
        />
        <TextInput
          style={styles.input}
          placeholder="Matricule"
          value={matricule}
          onChangeText={setMatricule}
        />
        <TouchableOpacity onPress={handleAddCar} style={styles.addButton}>
          <Text style={styles.buttonText}>Add Car</Text>
        </TouchableOpacity>
      </View>
      <FlatList
        data={cars}
        keyExtractor={(item) => item.id.toString()}
        renderItem={({ item }) => (
          <View style={styles.carItem}>
            <Text>{`${item.marque} ${item.model}`}</Text>
            <TouchableOpacity
              onPress={() => handleDeleteCar(item.id)}
              style={styles.deleteButton}
            >
              <Text style={styles.buttonText}>Delete</Text>
            </TouchableOpacity>
          </View>
        )}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
    padding: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: "bold",
    marginBottom: 20,
  },
  inputContainer: {
    marginBottom: 20,
  },
  input: {
    height: 40,
    borderWidth: 1,
    borderColor: "#ccc",
    borderRadius: 5,
    marginBottom: 10,
    paddingHorizontal: 10,
  },
  addButton: {
    backgroundColor: "#007AFF",
    padding: 10,
    borderRadius: 5,
    alignItems: "center",
  },
  buttonText: {
    color: "#fff",
    fontWeight: "bold",
  },
  carItem: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    marginBottom: 10,
  },
  deleteButton: {
    backgroundColor: "red",
    padding: 5,
    borderRadius: 5,
  },
});

export default CarsManagementCRUDScreen;
