import React from "react";
import { View, Text, StyleSheet } from "react-native";

const AgencyDetailsScreen = ({ route }) => {
  const { agency, token } = route.params;
  const [services, setServices] = useState([]);

  useEffect(() => {
    fetchAgencyServices();
  }, []);

  const fetchAgencyServices = async () => {
    try {
      const response = await axios.post(
        "http://127.0.0.1:8000/api/agencies/services/" + agency.id,
        {
          adresse: searchValue,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setServices(response.data.data);
    } catch (error) {
      console.error("Error fetching agency services :", error);
    }
  };

  const render = ({ item }) => (
    <TouchableOpacity
      style={styles.agencyItem}
      onPress={() => navigateToAgencyDetails(item)}
    >
      <Text style={styles.agencyName}>{item.name}</Text>
      <Text style={styles.agencyAddress}>{item.adresse}</Text>
    </TouchableOpacity>
  );

  return (
    <View style={styles.container}>
      <Text style={styles.title}>{agency.name}</Text>
      <Text style={styles.subtitle}>Address: {agency.adresse}</Text>
      <Text style={styles.subtitle}>Email: {agency.email}</Text>
      <Text style={styles.subtitle}>Téléphone: {agency.phone}</Text>
      <View>
        <Text>Liste des services disponibles</Text>
      </View>
      <FlatList
        data={services}
        renderItem={render}
        keyExtractor={(item) => item.id.toString()}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: "bold",
    marginBottom: 10,
  },
  subtitle: {
    fontSize: 18,
    fontWeight: "bold",
    marginTop: 20,
  },
  details: {
    fontSize: 16,
    marginTop: 5,
  },
});

export default AgencyDetailsScreen;
