import React, { useState, useEffect } from "react";
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  Modal,
  StyleSheet,
  Switch,
} from "react-native";
// import CheckBox from "@react-native-community/checkbox";
import axios from "axios";
import { CheckBox } from "react-native-web";

const AgenciesManagementCRUDScreen = ({ navigation, route }) => {
  const token = route.params.token;
  const [agencies, setAgencies] = useState([]);
  const [name, setName] = useState("");
  const [adresse, setAdresse] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [workDays, setWorkDays] = useState([]);
  const [start, setStart] = useState("");
  const [isDisabled, setIsDisabled] = useState(false);
  const [selectedAgencyId, setSelectedAgencyId] = useState(null);
  const [showWorkDaysModal, setShowWorkDaysModal] = useState(false);

  const toggleWorkDay = (day) => {
    if (selectedWorkDays.includes(day)) {
      setSelectedWorkDays(selectedWorkDays.filter((d) => d !== day));
    } else {
      setSelectedWorkDays([...selectedWorkDays, day]);
    }
  };

  useEffect(() => {
    // Fetch agencies data from an API endpoint or local storage
    fetchAgenciesData();
  }, []);

  const fetchAgenciesData = async () => {
    try {
      const response = await axios.get("http://127.0.0.1:8000/api/agencies", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setAgencies(response.data);
    } catch (error) {
      console.error("Error fetching agencies:", error);
    }
  };

  const handleAddAgency = async () => {
    const newAgency = {
      name,
      adresse,
      phone,
      email,
      workDays,
      start,
      isDisabled,
    };

    try {
      const response = await axios.post(
        "http://127.0.0.1:8000/api/agencies",
        newAgency,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      setAgencies([...agencies, response.data]);

      // Clear input fields
      setName("");
      setAdresse("");
      setPhone("");
      setEmail("");
      setWorkDays([]);
      setStart("");
      setIsDisabled(false);
    } catch (error) {
      console.error("Error adding agency:", error);
    }
  };

  const handleEditAgency = (agencyId) => {
    const agencyToEdit = agencies.find((agency) => agency.id === agencyId);
    setSelectedAgencyId(agencyId);
    setName(agencyToEdit.name);
    setAdresse(agencyToEdit.adresse);
    setPhone(agencyToEdit.phone);
    setEmail(agencyToEdit.email);
    setWorkDays(agencyToEdit.workDays);
    setStart(agencyToEdit.start);
    setIsDisabled(agencyToEdit.isDisabled);
  };

  const handleUpdateAgency = async () => {
    const updatedAgency = {
      name,
      adresse,
      phone,
      email,
      workDays,
      start,
      isDisabled,
    };

    try {
      const response = await axios.put(
        `http://127.0.0.1:8000/api/agencies/${selectedAgencyId}`,
        updatedAgency,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      const updatedAgencies = agencies.map((agency) =>
        agency.id === selectedAgencyId ? response.data : agency
      );

      setAgencies(updatedAgencies);

      // Clear input fields and selectedAgencyId
      setSelectedAgencyId(null);
      setName("");
      setAdresse("");
      setPhone("");
      setEmail("");
      setWorkDays([]);
      setStart("");
      setIsDisabled(false);
    } catch (error) {
      console.error("Error updating agency:", error);
    }
  };

  const handleDeleteAgency = async (agencyId) => {
    try {
      await axios.delete(`http://127.0.0.1:8000/api/agencies/${agencyId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      const updatedAgencies = agencies.filter(
        (agency) => agency.id !== agencyId
      );
      setAgencies(updatedAgencies);
    } catch (error) {
      console.error("Error deleting agency:", error);
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Agencies Management (CRUD)</Text>
      <View style={styles.inputContainer}>
        <TextInput
          style={styles.input}
          placeholder="Name"
          value={name}
          onChangeText={setName}
        />
        <TextInput
          style={styles.input}
          placeholder="Adresse"
          value={adresse}
          onChangeText={setAdresse}
        />
        <TextInput
          style={styles.input}
          placeholder="Phone"
          value={phone}
          onChangeText={setPhone}
        />
        <TextInput
          style={styles.input}
          placeholder="Email"
          value={email}
          onChangeText={setEmail}
          keyboardType="email-address"
        />
        <TouchableOpacity
          onPress={() => setShowWorkDaysModal(true)}
          style={styles.workDaysInput}
        >
          <Text>
            {workDays.length > 0 ? workDays.join(", ") : "Select Work Days"}
          </Text>
        </TouchableOpacity>
        <TextInput
          style={styles.input}
          placeholder="Start Time"
          value={start}
          onChangeText={setStart}
        />
        <View style={styles.switchContainer}>
          <Text style={styles.label}>Disable</Text>
          <Switch value={isDisabled} onValueChange={setIsDisabled} />
        </View>
        <TouchableOpacity onPress={handleAddAgency} style={styles.addButton}>
          <Text style={styles.buttonText}>Add Agency</Text>
        </TouchableOpacity>
      </View>

      {/* Work Days Modal */}
      {showWorkDaysModal && (
        <Modal
          visible={showWorkDaysModal}
          animationType="slide"
          transparent={true}
        >
          <View style={styles.modalContainer}>
            <View style={styles.modalContent}>
              <TouchableOpacity
                style={styles.checkbox}
                onPress={() => toggleWorkDay("Monday")}
              >
                <Text>Monday</Text>
                {selectedWorkDays.includes("Monday") && (
                  <Text style={styles.checkmark}>✓</Text>
                )}
              </TouchableOpacity>
              {/* ... Repeat for other days */}
              <TouchableOpacity
                onPress={() => setShowWorkDaysModal(false)}
                style={styles.closeButton}
              >
                <Text style={styles.closeButtonText}>Close</Text>
              </TouchableOpacity>
            </View>
          </View>
        </Modal>
      )}

      {/* ... (FlatList and editContainer) */}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: "bold",
    marginBottom: 20,
  },
  inputContainer: {
    borderWidth: 1,
    borderColor: "#ccc",
    borderRadius: 5,
    padding: 10,
    marginBottom: 20,
  },
  input: {
    height: 40,
    borderWidth: 1,
    borderColor: "#ccc",
    borderRadius: 5,
    marginBottom: 10,
    paddingHorizontal: 10,
  },
  label: {
    fontSize: 16,
    marginBottom: 5,
  },
  workDaysInput: {
    height: 40,
    borderWidth: 1,
    borderColor: "#ccc",
    borderRadius: 5,
    marginBottom: 10,
    paddingHorizontal: 10,
    justifyContent: "center",
  },
  modalContainer: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "rgba(0, 0, 0, 0.5)",
  },
  modalContent: {
    backgroundColor: "#fff",
    padding: 20,
    borderRadius: 10,
  },
  switchContainer: {
    flexDirection: "row",
    alignItems: "center",
    marginBottom: 10,
  },
  addButton: {
    backgroundColor: "#007AFF",
    padding: 10,
    borderRadius: 5,
    alignItems: "center",
  },
  buttonText: {
    color: "#fff",
    fontSize: 16,
  },
  closeButton: {
    marginTop: 10,
    alignSelf: "flex-end",
  },
  closeButtonText: {
    color: "blue",
  },
});

export default AgenciesManagementCRUDScreen;
