import React, { useState, useEffect } from "react";
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  FlatList,
  Picker,
  StyleSheet,
  Switch,
} from "react-native";
import axios from "axios";

const AgenciesManagementCRUDScreen = () => {
  const [agencies, setAgencies] = useState([]);
  const [name, setName] = useState("");
  const [adresse, setAdresse] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [workDays, setWorkDays] = useState([]);
  const [start, setStart] = useState("");
  const [isDisabled, setIsDisabled] = useState(false);
  const [selectedAgencyId, setSelectedAgencyId] = useState(null);

  useEffect(() => {
    // Fetch agencies data from an API endpoint or local storage
    fetchAgenciesData();
  }, []);

  const fetchAgenciesData = async () => {
    try {
      const response = await axios.get("http://127.0.0.1:8000/api/agencies");
      setAgencies(response.data);
    } catch (error) {
      console.error("Error fetching agencies:", error);
    }
  };

  const handleAddAgency = async () => {
    const newAgency = {
      name,
      adresse,
      phone,
      email,
      workDays,
      start,
      isDisabled,
    };

    try {
      const response = await axios.post(
        "http://127.0.0.1:8000/api/agencies",
        newAgency
      );

      setAgencies([...agencies, response.data]);

      // Clear input fields
      setName("");
      setAdresse("");
      setPhone("");
      setEmail("");
      setWorkDays([]);
      setStart("");
      setIsDisabled(false);
    } catch (error) {
      console.error("Error adding agency:", error);
    }
  };

  const handleEditAgency = (agencyId) => {
    const agencyToEdit = agencies.find((agency) => agency.id === agencyId);
    setSelectedAgencyId(agencyId);
    setName(agencyToEdit.name);
    setAdresse(agencyToEdit.adresse);
    setPhone(agencyToEdit.phone);
    setEmail(agencyToEdit.email);
    setWorkDays(agencyToEdit.workDays);
    setStart(agencyToEdit.start);
    setIsDisabled(agencyToEdit.isDisabled);
  };

  const handleUpdateAgency = async () => {
    const updatedAgency = {
      name,
      adresse,
      phone,
      email,
      workDays,
      start,
      isDisabled,
    };

    try {
      const response = await axios.put(
        `http://127.0.0.1:8000/api/agencies/${selectedAgencyId}`,
        updatedAgency
      );

      const updatedAgencies = agencies.map((agency) =>
        agency.id === selectedAgencyId ? response.data : agency
      );

      setAgencies(updatedAgencies);

      // Clear input fields and selectedAgencyId
      setSelectedAgencyId(null);
      setName("");
      setAdresse("");
      setPhone("");
      setEmail("");
      setWorkDays([]);
      setStart("");
      setIsDisabled(false);
    } catch (error) {
      console.error("Error updating agency:", error);
    }
  };

  const handleDeleteAgency = async (agencyId) => {
    try {
      await axios.delete(`http://127.0.0.1:8000/api/agencies/${agencyId}`);
      const updatedAgencies = agencies.filter(
        (agency) => agency.id !== agencyId
      );
      setAgencies(updatedAgencies);
    } catch (error) {
      console.error("Error deleting agency:", error);
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Agencies Management (CRUD)</Text>
      <View style={styles.inputContainer}>
        {/* ... (input fields, workDays selection, start time selection, and disable switch) */}
      </View>
      <FlatList
        data={agencies}
        keyExtractor={(item) => item.id.toString()}
        renderItem={({ item }) => (
          <View style={styles.agencyItem}>
            <Text>{item.name}</Text>
            <TouchableOpacity
              onPress={() => handleEditAgency(item.id)}
              style={styles.editButton}
            >
              <Text style={styles.buttonText}>Edit</Text>
            </TouchableOpacity>
            <TouchableOpacity
              onPress={() => handleDeleteAgency(item.id)}
              style={styles.deleteButton}
            >
              <Text style={styles.buttonText}>Delete</Text>
            </TouchableOpacity>
          </View>
        )}
      />
      {selectedAgencyId && (
        <View style={styles.editContainer}>
          {/* ... (input fields, workDays selection, start time selection, and disable switch for editing) */}
        </View>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  // ... (styles remain the same)
  editContainer: {
    backgroundColor: "#f9f9f9",
    padding: 10,
    marginBottom: 20,
  },
  editButton: {
    backgroundColor: "#007AFF",
    padding: 5,
    borderRadius: 5,
    marginRight: 10,
  },
  updateButton: {
    backgroundColor: "green",
    padding: 10,
    borderRadius: 5,
    alignItems: "center",
  },
});

export default AgenciesManagementCRUDScreen;
