import React from "react";
import { View, Text, StyleSheet } from "react-native";

const AgencyDetailsScreen = ({ route }) => {
  const { agency, token } = route.params;
  const [services, setServices] = useState([]);

  useEffect(() => {
    fetchAgenciesData();
  }, []);

  const fetchAgenciesData = async () => {
    try {
      const response = await axios.post(
        "http://127.0.0.1:8000/api/agencies/services/" + agency.id,
        {
          adresse: searchValue,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      setServices(response.data.data);
    } catch (error) {
      console.error("Error fetching agencies:", error);
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>{agency.name}</Text>
      <Text style={styles.subtitle}>Address: {agency.adresse}</Text>
      <Text style={styles.subtitle}>Email: {agency.email}</Text>
      <Text style={styles.subtitle}>Téléphone: {agency.phone}</Text>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: "bold",
    marginBottom: 10,
  },
  subtitle: {
    fontSize: 18,
    fontWeight: "bold",
    marginTop: 20,
  },
  details: {
    fontSize: 16,
    marginTop: 5,
  },
});

export default AgencyDetailsScreen;
